(89, '95f3795a-dd54-4eca-9832-4085dcf0bdf6', 2, 'Update', 'App\\Product', 34, 'App\\Product', 34, 'App\\Product', 34, '', 'finished', '', '2022-03-31 12:24:15', '2022-03-31 12:24:15', '{\"is_natural\":0,\"cosmos_compliant\":0,\"background_color\":\"#DDEBCE\",\"seo\":null}', '{\"is_natural\":false,\"cosmos_compliant\":false,\"background_color\":\"#ddebce\",\"seo\":\"{\\\"title\\\":\\\"Evicide\\\\u00ae hyacinth a multifunctional with strong fungicidal activity\\\",\\\"description\\\":\\\"Evicide\\\\u00ae hyacinth (INCI Phenylpropanol) is a valuable tool in preservation systems. As a fragrance component it imparts a pleasant floral smell and at the same time adds an antifungal effect to the formulation. Very useful against Mold (e.g. Aspergillus brasiliensis)\\\"}\"}'),
(90, '95f3b589-c862-4635-aa15-41c8ae4a40d1', 2, 'Update', 'App\\Product', 33, 'App\\Product', 33, 'App\\Product', 33, '', 'finished', '', '2022-03-31 15:12:32', '2022-03-31 15:12:32', '{\"description\":\"The vegetable derived Evicide\\u00ae GML belongs to the first actives discovered and used for alternative preservation. An Oldie but Goldie.\",\"benefits\":\"Evicide\\u00ae GML is a 100% natural Multifunctional ingredient. It has a well known antimicrobial effect and has been used for decades in alternative preservation systems. In contrast to other surfactant based antimicrobials Evicide\\u00ae GML does not have an impact on emulsion stability and is very mild on the skin, too. With its monoester content of 90% it is highly concentrated to achieve best results in the formulation.\",\"applications\":\"Evicide\\u00ae GML can be added to the water phase and requires melting at approximately 40-45\\u00b0 C. Mix it well prior to addition of the oil phase and homogenization.\",\"is_natural\":1,\"cosmos_compliant\":1,\"seo\":null}', '{\"description\":\"The vegetable derived Evicide\\u00ae GML belongs to the first actives discovered and used for preservative boosting and alternative preservation. An Oldie but Goldie.\",\"benefits\":\"Evicide\\u00ae GML is a 100% natural Multifunctional ingredient. It has a well known antimicrobial effect and has been used for decades in alternative preservation systems. In contrast to other surfactant based antimicrobials Evicide\\u00ae GML does not have an impact on emulsion stability and is very mild on the skin, too. With its monoester content of 90% it is highly concentrated to achieve best results in the formulation. On top of the boosting activity, we discovered much more, like its unique co-emulsifying properties and thickening of sulfate-free surfactant systems.\",\"applications\":\"Evicide\\u00ae GML can be added to the water or oil phase and requires melting at approximately 40-45\\u00b0 C. Mix it well prior to addition of the oil phase and homogenization. In emulsions it acts like an accelerator for the homogenization step because it helps the main emulsifiers to assemble more quickly at the interphase. The boosting effect can be used for every preservative system and will help to reduce the load of preservatives.\",\"is_natural\":true,\"cosmos_compliant\":true,\"seo\":\"{\\\"title\\\":\\\"Evicide\\\\u00ae GML is a natural co-emulsifier, preservative boosting agent and surfactant thickener\\\",\\\"description\\\":\\\"Evicide\\\\u00ae GML (INCI Glyceryl Laurate) is a true multifunctional for personal care products. The functions enclose co-emulsifying in skin care, efficacy boosting of preservative systems and thickening of sulfate-free rinse-off products.\\\"}\"}'),
