(268, '9a69e250-338d-44cb-8d8e-cd606f2bd0fe', 2, 'Create', 'App\\Product', 93, 'App\\Product', 93, 'App\\Product', 93, '', 'finished', '', '2023-10-20 09:53:11', '2023-10-20 09:53:11', NULL, '{\"name\":\"Evicare hyaluron XM\",\"trade_name\":\"Evicare\\u00ae hyaluron XM\",\"seo\":{\"title\":null,\"description\":null},\"description\":\"A Sodium Hyaluronate quality with medium molecular weight (100-500 kDa).\",\"is_natural\":true,\"cosmos_compliant\":true,\"usage_level\":\"0.05 - 0.5%\",\"inci\":\"Sodium Hyaluronate\",\"benefits\":\"Sodium Hyaluronate in this molecular weight range combines the advantages of the short chain hyaluronates (great absorbance) with those of the long chain hyaluronates (high moisturization). It is thus a highly active skin nourishing agent with deep penetration.\",\"applications\":\"Use in aqueous-based formulations such as micellar waters, or caring emulsions. Combine with other hyaluronate qualites for a broader effect.\",\"category_id\":3,\"slug\":\"evicare-hyaluron-xm\",\"updated_at\":\"2023-10-20T09:53:11.000000Z\",\"created_at\":\"2023-10-20T09:53:11.000000Z\",\"id\":93}'),
(269, '9a69e263-2194-4bd8-8342-d2f561c4683a', 2, 'Create', 'App\\Product', 94, 'App\\Product', 94, 'App\\Product', 94, '', 'finished', '', '2023-10-20 09:53:23', '2023-10-20 09:53:23', NULL, '{\"name\":\"Evicide PBS\",\"trade_name\":\"Evicide\\u00ae PBS\",\"seo\":{\"title\":null,\"description\":null},\"description\":\"Strong, Phenoxyethanol-based preservation blend with Benzyl Alcohol and Potassium Sorbate\",\"is_natural\":false,\"cosmos_compliant\":false,\"usage_level\":\"1.0 - 2.0%\",\"inci\":\"Phenoxyethanol, Benzyl Alcohol, Potassium Sorbate, Aqua, Tocopherol\",\"benefits\":\"If you want to reduce the concentration of Phenoxyethanol, but not quite remove it completely - this blend is for you. Benzylalcohol replaces about half of the Phenoxyethanol, and for further efficacy enhancement Potassium Sorbate is added. The result is a highly efficient, broad spectrum preservation mixture: Evicide\\u00ae PBS.\",\"applications\":\"Suitable for both rinse-off and leave on concepts. This blend is water-soluble in the recommended usage concentration. The antimicrobial efficacy is highest if the pH is below 6.0\",\"category_id\":4,\"slug\":\"evicide-pbs\",\"updated_at\":\"2023-10-20T09:53:23.000000Z\",\"created_at\":\"2023-10-20T09:53:23.000000Z\",\"id\":94}'),
